mapper.insert:
		insert into plan(userIdx, title, startDate, endDate) 
		values(#{userIdx}, #{title}, #{startDate}, #{endDate});


            select last_insert_id()

		insert into daillyPlan(planIdx, content_id, dailyDate) 
		values (#{planIdx}, #{content_id}, #{dailyDate});


            select last_insert_id()
